import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const Test = () => {
    const [items1, setItems1] = useState([
        { id: 'item-1', content: 'Item 1' },
        { id: 'item-2', content: 'Item 2' },
        { id: 'item-3', content: 'Item 3' }
    ]);

    const [items2, setItems2] = useState([
        { id: 'item-4', content: 'Item 4' },
        { id: 'item-5', content: 'Item 5' },
        { id: 'item-6', content: 'Item 6' }
    ]);

    const handleDragEnd = (result) => {
        if (!result.destination) return;

        const sourceList = result.source.droppableId === 'list1' ? items1 : items2;
        const destinationList = result.destination.droppableId === 'list1' ? items1 : items2;

        const items = [...sourceList];
        const [reorderedItem] = items.splice(result.source.index, 1);
        items.splice(result.destination.index, 0, reorderedItem);

        if (result.source.droppableId === result.destination.droppableId) {
            if (result.source.droppableId === 'list1') {
                setItems1(items);
            } else {
                setItems2(items);
            }
        } else {
            if (destinationList === items1) {
                setItems1(items);
                setItems2(destinationList);
            } else {
                setItems1(destinationList);
                setItems2(items);
            }
        }
    };


    return (
        <DragDropContext onDragEnd={handleDragEnd}>
            <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                <Droppable droppableId="list1">
                    {(provided) => (
                        <div
                            ref={provided.innerRef}
                            {...provided.droppableProps}
                            style={{
                                width: '45%',
                                border: '1px solid #ccc',
                                padding: 8,
                                borderRadius: 4
                            }}
                        >
                            <h3>List 1</h3>
                            {items1.map((item, index) => (
                                <Draggable key={item.id} draggableId={item.id} index={index}>
                                    {(provided) => (
                                        <div
                                            ref={provided.innerRef}
                                            {...provided.draggableProps}
                                            {...provided.dragHandleProps}
                                            style={{
                                                userSelect: 'none',
                                                padding: 8,
                                                margin: '0 0 8px 0',
                                                backgroundColor: '#fff',
                                                border: '1px solid #ddd',
                                                borderRadius: 4,
                                                ...provided.draggableProps.style
                                            }}
                                        >
                                            {item.content}
                                        </div>
                                    )}
                                </Draggable>
                            ))}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>

                <Droppable droppableId="list2">
                    {(provided) => (
                        <div
                            ref={provided.innerRef}
                            {...provided.droppableProps}
                            style={{
                                width: '45%',
                                border: '1px solid #ccc',
                                padding: 8,
                                borderRadius: 4
                            }}
                        >
                            <h3>List 2</h3>
                            {items2.map((item, index) => (
                                <Draggable key={item.id} draggableId={item.id} index={index}>
                                    {(provided) => (
                                        <div
                                            ref={provided.innerRef}
                                            {...provided.draggableProps}
                                            {...provided.dragHandleProps}
                                            style={{
                                                userSelect: 'none',
                                                padding: 8,
                                                margin: '0 0 8px 0',
                                                backgroundColor: '#fff',
                                                border: '1px solid #ddd',
                                                borderRadius: 4,
                                                ...provided.draggableProps.style
                                            }}
                                        >
                                            {item.content}
                                        </div>
                                    )}
                                </Draggable>
                            ))}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            </div>
        </DragDropContext>
    );
};

export default Test;
