import React, { useState, useEffect } from 'react';
import { ReactAnglePicker } from 'react-angle-picker';
import './style_adm.css';
import * as XLSX from 'xlsx';

const AdminConsole = () => {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [loggedInData, setLoggedInData] = useState(null);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [excelData, setExcelData] = useState(null);
    const [selectedCell, setSelectedCell] = useState({ row: null, col: null });
    const [config, setConfig] = useState({});
    const [newConfig, setNewConfig] = useState({});
    const [newCSConfig, setNewCSConfig] = useState({});
    const [csharpScriptConfig, setCSharpScriptConfig] = useState({});
    const url = 'http://localhost:3001/';
    const [activeTab, setActiveTab] = useState(null);
    const [selectedOption, setSelectedOption] = useState('Vienlaidu');
    const [color, setColor] = useState('#ff0000');
    const [color1, setColor1] = useState('#ff0000');
    const [color2, setColor2] = useState('#00ff00');
    const [angle, setAngle] = React.useState(0);
    const [tableStyles, setTableStyles] = useState('');
    const [tableWrapperStyles, setTableWrapperStyles] = useState('');

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    const handleLogin = async () => {
        // Отправляем запрос на сервер для проверки логина и пароля
        try {
            const response = await fetch(url + 'login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ login, password }),
            });

            if (!response.ok) {
                console.error('Authentication failed. HTTP status:', response.status);
                // Обработка ошибки аутентификации
                return;
            }

            const data = await response.json();
            setLoggedInData(data);
            setIsLoggedIn(true);
        } catch (error) {
            console.error('Error during login:', error);
        }
    };

    const handleRunCSharpScript = async () => {
        try {
            const response = await fetch(url + 'runCSharpScript', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                console.error('Failed to run C# script on the server. HTTP status:', response.status);
                throw new Error('Failed to run C# script on the server');
            }

            const jsonData = await response.json();

            if (!jsonData.data.trim()) {
                console.error('Empty response data received from the server.');
                return;
            }

            console.log('Raw server response data:', jsonData.data);

            let dataArray;

            try {
                dataArray = JSON.parse(jsonData.data);
            } catch (parseError) {
                console.error('Error parsing JSON data:', parseError);
                return;
            }

            if (!Array.isArray(dataArray)) {
                console.error('Data is not an array:', dataArray);
                return;
            }

            console.log('Parsed server response data:', dataArray);

            const ws = XLSX.utils.json_to_sheet(dataArray);
            const wb = XLSX.utils.book_new();
            XLSX.utils.book_append_sheet(wb, ws, 'Sheet1');

            XLSX.writeFile(wb, 'data.xlsx');
        } catch (error) {
            console.error('Error during C# script execution:', error);
        }
    };

    useEffect(() => {
        // Вызывается при монтировании компонента
        getConfig(); // Получаем конфигурацию при загрузке
        getCSharpScriptConfig();
    }, []);

    const getConfig = async () => {
        try {
            const response = await fetch(url + 'getConfig');
            if (!response.ok) {
                console.error('Failed to fetch configuration. HTTP status:', response.status);
                return;
            }
            const data = await response.json();
            setConfig(data.config);
        } catch (error) {
            console.error('Error fetching configuration:', error);
        }
    };

    const getCSharpScriptConfig = async () => {
        try {
            const response = await fetch(url + 'getCSharpScriptConfig');
            if (!response.ok) {
                console.error('Failed to fetch C# Script configuration. HTTP status:', response.status);
                return;
            }
            const data = await response.json();
            // Сохранение данных в состоянии React
            setCSharpScriptConfig(data);
        } catch (error) {
            console.error('Error fetching C# Script configuration:', error);
        }
    };

    const handleOptionChange = (option) => {
        setSelectedOption(option);
    };

    const handleColorChange = (color, setColor) => {
        // Обработчик изменения цвета
        setColor(color);

        // Попробуйте добавить следующий код, чтобы убедиться, что не теряется фокус на элементе
        setTimeout(() => {
            document.activeElement.blur();
        }, 0);
    };

    const handleChange = (value) => {
        setAngle(value);
    };

    const applySingleColor = async () => {
        try {
            const response = await fetch('http://localhost:3001/updateStyles', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ color })
            });
            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.error('Error applying single color:', error);
        }
    };

    const applyGradient = async () => {
        try {
            const response = await fetch('http://localhost:3001/updateStyles', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ color1, color2, angle })
            });
            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.error('Error applying gradient:', error);
        }
    };

    const resetToDefaultGradient = async () => {
        try {
            const defaultGradient = 'linear-gradient(229.82deg, #FFC400 18.72%, #FF6B00 84.82%)';
            const response = await fetch('http://localhost:3001/updateStyles', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ defaultGradient })
            });
            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.error('Error resetting to default gradient:', error);
        }
    };

    const applyTableStyles = async (tableStyles) => {
        try {
            const response = await fetch('http://localhost:3001/updateTableStyles', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ tableStyles })
            });
            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.error('Error applying table styles:', error);
        }
    };


    //unused
    const handleLoadExcel = async () => {
        try {
            const response = await fetch('https://docs.google.com/spreadsheets/d/1MBboSLNI-avCAJ7RZPeJKL-JPIuFY4H9eBoq3W18Qao/export?format=xlsx');
            const blob = await response.blob();
            const reader = new FileReader();

            reader.onload = async (e) => {
                const data = e.target.result;
                const workbook = XLSX.read(data, { type: 'binary' });
                const firstSheetName = workbook.SheetNames[0];
                const sheetData = XLSX.utils.sheet_to_json(workbook.Sheets[firstSheetName], { defval: '' });

                if (Array.isArray(sheetData) && sheetData.length > 0) {
                    const formattedData = [Object.keys(sheetData[0]), ...sheetData.map(row => Object.values(row))];
                    console.log('Formatted Excel Data:', formattedData);
                    setExcelData(formattedData);
                } else {
                    console.error('Invalid Excel data format.');
                }
            };

            reader.readAsBinaryString(blob);
        } catch (error) {
            console.error('Error loading Excel file:', error);
        }
    };
    const handleCellClick = (rowIndex, colIndex) => {
        setSelectedCell({ row: rowIndex, col: colIndex });
    };

    const handleUpdateCSharpScriptConfig = async () => {
        try {
            const updates = Object.entries(newCSConfig).map(([key, value]) => ({ key, value }));
            console.log(updates);
            const response = await fetch(url + 'updateCSharpScriptConfig', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updates),
            });

            if (!response.ok) {
                console.error('Failed to update C# script configuration. HTTP status:', response.status);
                throw new Error('Failed to update C# script configuration');
            }

            const updateResponses = await response.json(); // Обратите внимание на "s" в конце

            updateResponses.forEach(updateResponse => {
                if (updateResponse.success) {
                    console.log(`C# script configuration updated successfully. Old value: ${updateResponse.oldValue}`);
                } else {
                    console.error('Failed to update C# script configuration. Server response:', updateResponse);
                }
            });

            getCSharpScriptConfig();

        } catch (error) {
            console.error('Error updating C# script configuration:', error);
        }
    };

    const handleUpdateConfig = async () => {
        try {
            const updates = Object.entries(newConfig).map(([key, value]) => ({ key, value }));

            const response = await fetch(url + 'updateConfig', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updates),
            });

            if (!response.ok) {
                console.error('Failed to update configuration. HTTP status:', response.status);
                throw new Error('Failed to update configuration');
            }

            const updateResponse = await response.json();

            if (updateResponse.success) {
                console.log('Configuration updated successfully. Old value:', updateResponse.oldValue);
                getConfig();
            } else {
                console.error('Failed to update configuration. Server response:', updateResponse);
            }

        } catch (error) {
            console.error('Error updating configuration:', error);
        }
    };

    const handleInputChangeCSConfig = (key, value) => {
        setNewCSConfig((prevConfig) => ({
            ...prevConfig,
            [key]: value,
        }));
    };

    const handleInputChange = (key, value) => {
        setNewConfig((prevConfig) => ({
            ...prevConfig,
            [key]: value,
        }));
    };

    return (
        <div>
            {!isLoggedIn && (
                <>
                    <div className="admin-form">
                        <div>
                            <label htmlFor="login">Login:</label>
                            <input
                                type="text"
                                id="login"
                                value={login}
                                onChange={(e) => setLogin(e.target.value)}
                            />
                        </div>
                        <div>
                            <label htmlFor="password">Password:</label>
                            <input
                                type="password"
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>
                        <button onClick={handleLogin}>Enter</button>
                    </div>
                </>
            )}
            {isLoggedIn && (
                <div>
                    <div className="tab-container">
                        <button
                            onClick={() => handleTabClick('aizvietosanas')}
                            className={activeTab === 'aizvietosanas' ? 'active-tab' : ''}
                        >
                            Aizvietošanas
                        </button>

                        <button
                            onClick={() => handleTabClick('atteli')}
                            className={activeTab === 'atteli' ? 'active-tab' : ''}
                        >
                            Attēli
                        </button>

                        <button
                            onClick={() => handleTabClick('iestatijumi')}
                            className={activeTab === 'iestatijumi' ? 'active-tab' : ''}
                        >
                            Iestatījumi
                        </button>
                    </div>
                    {/* Отображение контента в зависимости от выбранного таба */}
                    {activeTab === 'aizvietosanas' && (
                        <div className="content-container">
                            {/* Вставьте содержимое компонента AizvietosanasTab */}
                            <button
                                className={selectedOption === 'Vienlaidu' ? 'active-tab' : ''}
                                onClick={() => handleOptionChange('Vienlaidu')}
                            >
                                Vienlaidu
                            </button>
                            <button
                                className={selectedOption === 'Gradients' ? 'active-tab' : ''}
                                onClick={() => handleOptionChange('Gradients')}
                            >
                                Gradients
                            </button>
                            <button
                                className={selectedOption === 'tabula' ? 'active-tab' : ''}
                                onClick={() => handleOptionChange('tabula')}
                            >
                                Tabula
                            </button>
                            {selectedOption === 'tabula' && (
                                <div>
                                    {/* Элементы для настройки стилей таблицы */}
                                    <input
                                        type="text"
                                        placeholder="Enter table wrapper styles"
                                        value={tableWrapperStyles}
                                        onChange={(e) => setTableWrapperStyles(e.target.value)}
                                    />
                                    <input
                                        type="text"
                                        placeholder="Enter table styles"
                                        value={tableStyles}
                                        onChange={(e) => setTableStyles(e.target.value)}
                                    />
                                    <button onClick={applyTableStyles}>Apply Table Styles</button>
                                    <div className="table-wrapper" style={{ position: 'center', top: '10vh', left: '0', transform: 'translate(10vh, 0) scale(0.5)' }}>
                                        <table className="fl-table">
                                            <thead>
                                                <tr>
                                                    <th>Aizvietotājs</th>
                                                    <th>Stunda</th>
                                                    <th>Klase</th>
                                                    <th>Kab.</th>
                                                    <th>Promešosais skolotājs</th>
                                                    <th>Piezīmes</th>
                                                </tr>
                                            </thead>
                                            <tbody className="dataBody">
                                                <tr>
                                                    <td>John Doe</td>
                                                    <td>9:00</td>
                                                    <td>11A</td>
                                                    <td>101</td>
                                                    <td>Jane Smith</td>
                                                    <td>No special notes</td>
                                                </tr>
                                                <tr>
                                                    <td>Jane Smith</td>
                                                    <td>10:00</td>
                                                    <td>10B</td>
                                                    <td>102</td>
                                                    <td>John Doe</td>
                                                    <td>No special notes</td>
                                                </tr>
                                                <tr>
                                                    <td>Michael Johnson</td>
                                                    <td>11:00</td>
                                                    <td>12C</td>
                                                    <td>103</td>
                                                    <td>Mary Wilson</td>
                                                    <td>Bring extra materials</td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            )}

                            {selectedOption === 'Vienlaidu' && (
                                <div>
                                    {/* Элементы для Vienlaidu */}
                                    <input className="custom-style"
                                        type="color"
                                        value={color}
                                        onChange={(e) => handleColorChange(e.target.value, setColor)}
                                    />
                                    <button onClick={applySingleColor}>Apply Single Color</button>
                                </div>
                            )}

                            {selectedOption === 'Gradients' && (
                                <div>
                                    {/* Элементы для Gradients */}
                                    <input className="custom-style"
                                        type="color"
                                        value={color1}
                                        onChange={(e) => handleColorChange(e.target.value, setColor1)}
                                    />
                                    <input className="custom-style"
                                        type="color"
                                        value={color2}
                                        onChange={(e) => handleColorChange(e.target.value, setColor2)}
                                    />
                                    <div>Gradient Angle:</div>
                                    <ReactAnglePicker
                                        angle={angle}
                                        onChange={handleChange}
                                        onFinalChange={handleChange} // Для мгновенного изменения угла
                                    />
                                    {angle}
                                    <button onClick={applyGradient}>Apply Gradient</button>
                                </div>
                            )}
                            <button onClick={resetToDefaultGradient}>Reset to Default</button>
                        </div>
                    )}
                    {activeTab === 'atteli' && (
                        <div className="content-container">
                            {/* Содержимое компонента AtteliTab */}
                            <div>Attēli Tab Content</div>
                        </div>
                    )}
                    {activeTab === 'iestatijumi' && (
                        <div className="content-container">
                            {/* Содержимое компонента IestatijumiTab */}
                            <div>Iestatījumi Tab Content</div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default AdminConsole;
