import React, { useState, useEffect } from 'react';
import { ReactAnglePicker } from 'react-angle-picker';
import './style_adm.css';
import * as XLSX from 'xlsx';

const AdminConsole = () => {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [loggedInData, setLoggedInData] = useState(null);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [excelData, setExcelData] = useState(null);
    const [selectedCell, setSelectedCell] = useState({ row: null, col: null });
    const [config, setConfig] = useState({});
    const [newConfig, setNewConfig] = useState({});
    const [newCSConfig, setNewCSConfig] = useState({});
    const [csharpScriptConfig, setCSharpScriptConfig] = useState({});
    const url = 'http://localhost:3001/';
    const [activeTab, setActiveTab] = useState(null);
    const [selectedOption, setSelectedOption] = useState('Vienlaidu');
    const [color, setColor] = useState('#ff0000');
    const [color1, setColor1] = useState('#ff0000');
    const [color2, setColor2] = useState('#00ff00');
    const [angle, setAngle] = React.useState(0);
    const [tableColor, setTableColor] = useState('');
    const [tableWrapperFontSize, setTableWrapperFontSize] = useState('');
    const [backgroundType, setBackgroundType] = useState('');
    const [headerStyles, setHeaderStyles] = useState({
        fontSize: '',
        fontFamily: '',
        fontWeight: '',
        fontStyle: '',
        textTransform: ''
    });
    const [cellStyles, setCellStyles] = useState({
        fontSize: '',
        fontFamily: '',
        fontWeight: '',
        fontStyle: '',
        textTransform: ''
    });
    const fontFamilies = [
        'Arial',
        'Verdana',
        'Tahoma',
        'Times New Roman',
        'Georgia',
        'Courier New',
        // Добавьте другие варианты семейств шрифтов по желанию
    ];
    const units = ['px', '%', 'vw', 'vh'];


    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    const handleLogin = async () => {
        // Отправляем запрос на сервер для проверки логина и пароля
        try {
            const response = await fetch(url + 'login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ login, password }),
            });

            if (!response.ok) {
                console.error('Authentication failed. HTTP status:', response.status);
                // Обработка ошибки аутентификации
                return;
            }

            const data = await response.json();
            setLoggedInData(data);
            setIsLoggedIn(true);
        } catch (error) {
            console.error('Error during login:', error);
        }
    };

    const handleRunCSharpScript = async () => {
        try {
            const response = await fetch(url + 'runCSharpScript', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                console.error('Failed to run C# script on the server. HTTP status:', response.status);
                throw new Error('Failed to run C# script on the server');
            }

            const jsonData = await response.json();

            if (!jsonData.data.trim()) {
                console.error('Empty response data received from the server.');
                return;
            }

            console.log('Raw server response data:', jsonData.data);

            let dataArray;

            try {
                dataArray = JSON.parse(jsonData.data);
            } catch (parseError) {
                console.error('Error parsing JSON data:', parseError);
                return;
            }

            if (!Array.isArray(dataArray)) {
                console.error('Data is not an array:', dataArray);
                return;
            }

            console.log('Parsed server response data:', dataArray);

            const ws = XLSX.utils.json_to_sheet(dataArray);
            const wb = XLSX.utils.book_new();
            XLSX.utils.book_append_sheet(wb, ws, 'Sheet1');

            XLSX.writeFile(wb, 'data.xlsx');
        } catch (error) {
            console.error('Error during C# script execution:', error);
        }
    };

    useEffect(() => {
        // Вызывается при монтировании компонента
        getConfig(); // Получаем конфигурацию при загрузке
        getCSharpScriptConfig();
    }, []);

    const getConfig = async () => {
        try {
            const response = await fetch(url + 'getConfig');
            if (!response.ok) {
                console.error('Failed to fetch configuration. HTTP status:', response.status);
                return;
            }
            const data = await response.json();
            setConfig(data.config);
        } catch (error) {
            console.error('Error fetching configuration:', error);
        }
    };

    const getCSharpScriptConfig = async () => {
        try {
            const response = await fetch(url + 'getCSharpScriptConfig');
            if (!response.ok) {
                console.error('Failed to fetch C# Script configuration. HTTP status:', response.status);
                return;
            }
            const data = await response.json();
            // Сохранение данных в состоянии React
            setCSharpScriptConfig(data);
        } catch (error) {
            console.error('Error fetching C# Script configuration:', error);
        }
    };

    const handleOptionChange = (option) => {
        setSelectedOption(option);
    };

    const handleColorChange = (color, setColor) => {
        // Обработчик изменения цвета
        setColor(color);

        // Попробуйте добавить следующий код, чтобы убедиться, что не теряется фокус на элементе
        setTimeout(() => {
            document.activeElement.blur();
        }, 0);
    };

    const handleChange = (value) => {
        setAngle(value);
    };

    const applySingleColor = async () => {
        try {
            const response = await fetch('http://localhost:3001/updateStyles', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ color })
            });
            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.error('Error applying single color:', error);
        }
    };

    const applyGradient = async () => {
        try {
            const response = await fetch('http://localhost:3001/updateStyles', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ color1, color2, angle })
            });
            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.error('Error applying gradient:', error);
        }
    };

    const resetToDefaultGradient = async () => {
        try {
            const defaultGradient = 'linear-gradient(229.82deg, #FFC400 18.72%, #FF6B00 84.82%)';
            const response = await fetch('http://localhost:3001/updateStyles', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ defaultGradient })
            });
            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.error('Error resetting to default gradient:', error);
        }
    };

    const applyTableStyles = async (tableStyles) => {
        try {
            const response = await fetch('http://localhost:3001/updateTableStyles', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ tableStyles })
            });
            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.error('Error applying table styles:', error);
        }
    };

    const handleHeaderFontSizeChange = (value, type) => {
        setHeaderStyles(prevHeaderStyles => ({ ...prevHeaderStyles, fontSize: value + type }));
    };

    const handleCellFontSizeChange = (value, type) => {
        setCellStyles(prevCellStyles => ({ ...prevCellStyles, fontSize: value + type }));
    };



    //unused
    const handleLoadExcel = async () => {
        try {
            const response = await fetch('https://docs.google.com/spreadsheets/d/1MBboSLNI-avCAJ7RZPeJKL-JPIuFY4H9eBoq3W18Qao/export?format=xlsx');
            const blob = await response.blob();
            const reader = new FileReader();

            reader.onload = async (e) => {
                const data = e.target.result;
                const workbook = XLSX.read(data, { type: 'binary' });
                const firstSheetName = workbook.SheetNames[0];
                const sheetData = XLSX.utils.sheet_to_json(workbook.Sheets[firstSheetName], { defval: '' });

                if (Array.isArray(sheetData) && sheetData.length > 0) {
                    const formattedData = [Object.keys(sheetData[0]), ...sheetData.map(row => Object.values(row))];
                    console.log('Formatted Excel Data:', formattedData);
                    setExcelData(formattedData);
                } else {
                    console.error('Invalid Excel data format.');
                }
            };

            reader.readAsBinaryString(blob);
        } catch (error) {
            console.error('Error loading Excel file:', error);
        }
    };
    const handleCellClick = (rowIndex, colIndex) => {
        setSelectedCell({ row: rowIndex, col: colIndex });
    };

    const handleUpdateCSharpScriptConfig = async () => {
        try {
            const updates = Object.entries(newCSConfig).map(([key, value]) => ({ key, value }));
            console.log(updates);
            const response = await fetch(url + 'updateCSharpScriptConfig', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updates),
            });

            if (!response.ok) {
                console.error('Failed to update C# script configuration. HTTP status:', response.status);
                throw new Error('Failed to update C# script configuration');
            }

            const updateResponses = await response.json(); // Обратите внимание на "s" в конце

            updateResponses.forEach(updateResponse => {
                if (updateResponse.success) {
                    console.log(`C# script configuration updated successfully. Old value: ${updateResponse.oldValue}`);
                } else {
                    console.error('Failed to update C# script configuration. Server response:', updateResponse);
                }
            });

            getCSharpScriptConfig();

        } catch (error) {
            console.error('Error updating C# script configuration:', error);
        }
    };

    const handleUpdateConfig = async () => {
        try {
            const updates = Object.entries(newConfig).map(([key, value]) => ({ key, value }));

            const response = await fetch(url + 'updateConfig', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updates),
            });

            if (!response.ok) {
                console.error('Failed to update configuration. HTTP status:', response.status);
                throw new Error('Failed to update configuration');
            }

            const updateResponse = await response.json();

            if (updateResponse.success) {
                console.log('Configuration updated successfully. Old value:', updateResponse.oldValue);
                getConfig();
            } else {
                console.error('Failed to update configuration. Server response:', updateResponse);
            }

        } catch (error) {
            console.error('Error updating configuration:', error);
        }
    };

    const handleInputChangeCSConfig = (key, value) => {
        setNewCSConfig((prevConfig) => ({
            ...prevConfig,
            [key]: value,
        }));
    };

    const handleInputChange = (key, value) => {
        setNewConfig((prevConfig) => ({
            ...prevConfig,
            [key]: value,
        }));
    };

    return (
        <div>
            {!isLoggedIn && (
                <>
                    <div className="admin-form">
                        <div>
                            <label htmlFor="login">Login:</label>
                            <input
                                type="text"
                                id="login"
                                value={login}
                                onChange={(e) => setLogin(e.target.value)}
                            />
                        </div>
                        <div>
                            <label htmlFor="password">Password:</label>
                            <input
                                type="password"
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>
                        <button onClick={handleLogin}>Enter</button>
                    </div>
                </>
            )}
            {isLoggedIn && (
                <div>
                    <div className="tab-container">
                        <button
                            onClick={() => handleTabClick('aizvietosanas')}
                            className={activeTab === 'aizvietosanas' ? 'active-tab' : ''}
                        >
                            Aizvietošanas
                        </button>

                        <button
                            onClick={() => handleTabClick('atteli')}
                            className={activeTab === 'atteli' ? 'active-tab' : ''}
                        >
                            Attēli
                        </button>

                        <button
                            onClick={() => handleTabClick('iestatijumi')}
                            className={activeTab === 'iestatijumi' ? 'active-tab' : ''}
                        >
                            Iestatījumi
                        </button>
                    </div>
                    {/* Отображение контента в зависимости от выбранного таба */}
                    {activeTab === 'aizvietosanas' && (
                        <div className="content-container">
                            {/* Вставьте содержимое компонента AizvietosanasTab */}
                            <button
                                className={selectedOption === 'Vienlaidu' ? 'active-tab' : ''}
                                onClick={() => handleOptionChange('Vienlaidu')}
                            >
                                Vienlaidu
                            </button>
                            <button
                                className={selectedOption === 'Gradients' ? 'active-tab' : ''}
                                onClick={() => handleOptionChange('Gradients')}
                            >
                                Gradients
                            </button>
                            <button
                                className={selectedOption === 'tabula' ? 'active-tab' : ''}
                                onClick={() => handleOptionChange('tabula')}
                            >
                                Tabula
                            </button>
                            {selectedOption === 'tabula' && (
                                <div>
                                    {/* Элементы для настройки стилей таблицы */}
                                    <h4>Header Styles:</h4>
                                    <label htmlFor="headerFontSize">Font Size:</label>
                                    <input
                                        type="text"
                                        id="headerFontSize"
                                        placeholder="Enter font size for table header"
                                        value={headerStyles.fontSize.replace(/\D/g, '')} // Отображаем только цифры
                                        onChange={(e) => handleHeaderFontSizeChange(e.target.value, headerStyles.fontSize.replace(/\d/g, ''))}
                                    />

                                    <select
                                        value={headerStyles.fontSize.replace(/\d+/g, '')} // Отображаем только единицы измерения
                                        onChange={(e) => handleHeaderFontSizeChange(headerStyles.fontSize.replace(/\D/g, ''), e.target.value)}
                                    >
                                        {units.map(unit => (
                                            <option key={unit} value={unit}>{unit}</option>
                                        ))}
                                    </select>

                                    <label htmlFor="headerFontFamily">Font Family:</label>
                                    <select
                                        id="headerFontFamily"
                                        value={headerStyles.fontFamily}
                                        onChange={(e) => setHeaderStyles({ ...headerStyles, fontFamily: e.target.value })}
                                    >
                                        <option value="">Select font family</option>
                                        {fontFamilies.map((font, index) => (
                                            <option key={index} value={font}>{font}</option>
                                        ))}
                                    </select>

                                    <label htmlFor="headerFontWeight">Font Weight:</label>
                                    <select
                                        id="headerFontWeight"
                                        value={headerStyles.fontWeight}
                                        onChange={(e) => setHeaderStyles({ ...headerStyles, fontWeight: e.target.value })}
                                    >
                                        <option value="">Select font weight</option>
                                        <option value="normal">Normal</option>
                                        <option value="bold">Bold</option>
                                        {/* Добавьте другие варианты толщины шрифта для шапки таблицы здесь */}
                                    </select>

                                    {/* Добавьте другие параметры стилей для шапки таблицы здесь */}

                                    <h4>Cell Styles:</h4>
                                    <label htmlFor="cellFontSize">Font Size:</label>
                                    <input
                                        type="text"
                                        id="cellFontSize"
                                        placeholder="Enter font size for table cells"
                                        value={cellStyles.fontSize.replace(/\D/g, '')} // Отображаем только цифры
                                        onChange={(e) => handleCellFontSizeChange(e.target.value, cellStyles.fontSize.replace(/\d/g, ''))}
                                    />

                                    <select
                                        value={cellStyles.fontSize.replace(/\d+/g, '')} // Отображаем только единицы измерения
                                        onChange={(e) => handleCellFontSizeChange(cellStyles.fontSize.replace(/\D/g, ''), e.target.value)}
                                    >
                                        {units.map(unit => (
                                            <option key={unit} value={unit}>{unit}</option>
                                        ))}
                                    </select>

                                    <label htmlFor="cellFontFamily">Font Family:</label>
                                    <select
                                        id="cellFontFamily"
                                        value={cellStyles.fontFamily}
                                        onChange={(e) => setCellStyles({ ...cellStyles, fontFamily: e.target.value })}
                                    >
                                        <option value="">Select font family</option>
                                        {fontFamilies.map((font, index) => (
                                            <option key={index} value={font}>{font}</option>
                                        ))}
                                    </select>

                                    <label htmlFor="cellFontWeight">Font Weight:</label>
                                    <select
                                        id="cellFontWeight"
                                        value={cellStyles.fontWeight}
                                        onChange={(e) => setCellStyles({ ...cellStyles, fontWeight: e.target.value })}
                                    >
                                        <option value="">Select font weight</option>
                                        <option value="normal">Normal</option>
                                        <option value="bold">Bold</option>
                                        {/* Добавьте другие варианты толщины шрифта для ячеек таблицы здесь */}
                                    </select>
                                    <br /><br/>
                                    <button onClick={applyTableStyles}>Apply Table Styles</button>
                                    {/* Здесь добавлены стили в стиль атрибут */}
                                    
                                </div>
                            )}

                            {selectedOption === 'Vienlaidu' && (
                                <div>
                                    {/* Элементы для Vienlaidu */}
                                    <input className="custom-style"
                                        type="color"
                                        value={color}
                                        onChange={(e) => handleColorChange(e.target.value, setColor)}
                                    />
                                    <button onClick={applySingleColor}>Apply Single Color</button>
                                    <button onClick={resetToDefaultGradient}>Reset to Default</button>
                                </div>
                            )}

                            {selectedOption === 'Gradients' && (
                                <div>
                                    {/* Элементы для Gradients */}
                                    <input className="custom-style"
                                        type="color"
                                        value={color1}
                                        onChange={(e) => handleColorChange(e.target.value, setColor1)}
                                    />
                                    <input className="custom-style"
                                        type="color"
                                        value={color2}
                                        onChange={(e) => handleColorChange(e.target.value, setColor2)}
                                    />
                                    <div>Gradient Angle:</div>
                                    <ReactAnglePicker
                                        angle={angle}
                                        onChange={handleChange}
                                        onFinalChange={handleChange} // Для мгновенного изменения угла
                                    />
                                    {angle}
                                    <button onClick={applyGradient}>Apply Gradient</button>
                                    <button onClick={resetToDefaultGradient}>Reset to Default</button>
                                </div>
                            )}
                            <label>
                                Background Type:
                                <select
                                    value={backgroundType}
                                    onChange={(e) => setBackgroundType(e.target.value)}
                                >
                                    <option value="none">None</option>
                                    <option value="color">Color</option>
                                    <option value="gradient">Gradient</option>
                                </select>
                            </label>
                            <div className="table-wrapper" style={{
                                top: '30vh', left: '-10vw', transform: 'translate(0, 0) scale(0.6)', color: tableColor, background: backgroundType === 'gradient' ?
                                    `linear-gradient(${angle}deg, ${color1}, ${color2})` :
                                    backgroundType === 'color' ?
                                        color :
                                        'none'
                            }}>
                                <table className="fl-table">
                                    <thead>
                                        <tr style={{ border: "3px solid " + tableColor }}>
                                            <th style={{ border: "3px solid " + tableColor, fontFamily: headerStyles.fontFamily, fontWeight: headerStyles.fontWeight, fontStyle: headerStyles.fontStyle, textTransform: headerStyles.textTransform, color: headerStyles.color }}>Aizvietotājs</th>
                                            <th style={{ border: "3px solid " + tableColor, fontFamily: headerStyles.fontFamily, fontWeight: headerStyles.fontWeight, fontStyle: headerStyles.fontStyle, textTransform: headerStyles.textTransform, color: headerStyles.color }}>Stunda</th>
                                            <th style={{ border: "3px solid " + tableColor, fontFamily: headerStyles.fontFamily, fontWeight: headerStyles.fontWeight, fontStyle: headerStyles.fontStyle, textTransform: headerStyles.textTransform, color: headerStyles.color }}>Klase</th>
                                            <th style={{ border: "3px solid " + tableColor, fontFamily: headerStyles.fontFamily, fontWeight: headerStyles.fontWeight, fontStyle: headerStyles.fontStyle, textTransform: headerStyles.textTransform, color: headerStyles.color }}>Kab.</th>
                                            <th style={{ border: "3px solid " + tableColor, fontFamily: headerStyles.fontFamily, fontWeight: headerStyles.fontWeight, fontStyle: headerStyles.fontStyle, textTransform: headerStyles.textTransform, color: headerStyles.color }}>Promešosais skolotājs</th>
                                            <th style={{ border: "3px solid " + tableColor, fontFamily: headerStyles.fontFamily, fontWeight: headerStyles.fontWeight, fontStyle: headerStyles.fontStyle, textTransform: headerStyles.textTransform, color: headerStyles.color }}>Piezīmes</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>John Doe</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>1.</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>11A</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>101</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>Jane Smith</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>No special notes</td>
                                        </tr>
                                        <tr>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>Jane Smith</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>3.</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>10B</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>102</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>John Doe</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>No special notes</td>
                                        </tr>
                                        <tr>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>Michael Johnson</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>5.</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>12C</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>103</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>Mary Wilson</td>
                                            <td style={{ border: "3px solid " + tableColor, fontFamily: cellStyles.fontFamily, fontWeight: cellStyles.fontWeight, fontStyle: cellStyles.fontStyle, textTransform: cellStyles.textTransform, color: cellStyles.color }}>Bring extra materials</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    )}
                    {activeTab === 'atteli' && (
                        <div className="content-container">
                            {/* Содержимое компонента AtteliTab */}
                            <div>Attēli Tab Content</div>
                        </div>
                    )}
                    {activeTab === 'iestatijumi' && (
                        <div className="content-container">
                            {/* Содержимое компонента IestatijumiTab */}
                            <div>Iestatījumi Tab Content</div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default AdminConsole;
