import React, { useReducer } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const initialState = {
    items1: [
        { id: 'item-1', content: 'Item 1' },
        { id: 'item-2', content: 'Item 2' },
        { id: 'item-3', content: 'Item 3' }
    ],
    items2: [
        { id: 'item-4', content: 'Item 4' },
        { id: 'item-5', content: 'Item 5' },
        { id: 'item-6', content: 'Item 6' }
    ]
};

const reducer = (state, action) => {
    switch (action.type) {
        case 'DRAG_END':
            const { source, destination } = action.payload;
            if (!destination) return state;

            const sourceList = source.droppableId === 'list1' ? 'items1' : 'items2';
            const destinationList = destination.droppableId === 'list1' ? 'items1' : 'items2';

            const items = [...state[sourceList]];
            const [reorderedItem] = items.splice(source.index, 1);
            items.splice(destination.index, 0, reorderedItem);

            if (source.droppableId === destination.droppableId) {
                return { ...state, [sourceList]: items };
            } else {
                return { ...state, [sourceList]: items, [destinationList]: items };
            }
        default:
            return state;
    }
};

const Test = () => {
    const [state, dispatch] = useReducer(reducer, initialState);

    const handleDragEnd = (result) => {
        dispatch({ type: 'DRAG_END', payload: result });
    };

    return (
        <DragDropContext onDragEnd={handleDragEnd}>
            <div style={{ display: 'flex', justifyContent: 'space-around' }}>
                <Droppable droppableId="list1">
                    {(provided) => (
                        <div
                            ref={provided.innerRef}
                            {...provided.droppableProps}
                            style={{
                                width: '45%',
                                border: '1px solid #ccc',
                                padding: 8,
                                borderRadius: 4
                            }}
                        >
                            <h3>List 1</h3>
                            {state.items1.map((item, index) => (
                                <Draggable key={item.id} draggableId={item.id} index={index}>
                                    {(provided) => (
                                        <div
                                            ref={provided.innerRef}
                                            {...provided.draggableProps}
                                            {...provided.dragHandleProps}
                                            style={{
                                                userSelect: 'none',
                                                padding: 8,
                                                margin: '0 0 8px 0',
                                                backgroundColor: '#fff',
                                                border: '1px solid #ddd',
                                                borderRadius: 4,
                                                ...provided.draggableProps.style
                                            }}
                                        >
                                            {item.content}
                                        </div>
                                    )}
                                </Draggable>
                            ))}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>

                <Droppable droppableId="list2">
                    {(provided) => (
                        <div
                            ref={provided.innerRef}
                            {...provided.droppableProps}
                            style={{
                                width: '45%',
                                border: '1px solid #ccc',
                                padding: 8,
                                borderRadius: 4
                            }}
                        >
                            <h3>List 2</h3>
                            {state.items2.map((item, index) => (
                                <Draggable key={item.id} draggableId={item.id} index={index}>
                                    {(provided) => (
                                        <div
                                            ref={provided.innerRef}
                                            {...provided.draggableProps}
                                            {...provided.dragHandleProps}
                                            style={{
                                                userSelect: 'none',
                                                padding: 8,
                                                margin: '0 0 8px 0',
                                                backgroundColor: '#fff',
                                                border: '1px solid #ddd',
                                                borderRadius: 4,
                                                ...provided.draggableProps.style
                                            }}
                                        >
                                            {item.content}
                                        </div>
                                    )}
                                </Draggable>
                            ))}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            </div>
        </DragDropContext>
    );
};

export default Test;
