import React, { useState, useEffect } from 'react';
import Data_table from './Data_table';
import './App.css';
const { exec } = require('child_process');

const runCSharpScript = () => {
    return new Promise((resolve, reject) => {
        const csharpScriptPath = 'D:\\servers\\SheetsInfo\\SheetsInfo\\bin\\Debug\\net8.0\\SheetsInfo.exe';
        const credentialsPath = '.\\credentials.json';

        const command = `${csharpScriptPath} ${credentialsPath}`;

        exec(command, (error, stdout, stderr) => {
            if (error) {
                reject(error);
                return;
            }

            if (stderr) {
                reject(stderr);
                return;
            }

            try {
                const jsonData = JSON.parse(stdout);
                resolve(jsonData);
            } catch (jsonError) {
                reject(jsonError);
            }
        });
    });
};

function App() {
    const [currentTime, setCurrentTime] = useState(getFormattedTime());
    const [currentDateLV, setCurrentDateLV] = useState(getFormattedLatvianDate());
    const [currentDate, setCurrentDate] = useState(getFormattedDate());
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage, setItemsPerPage] = useState(10);
    const [maxPageButtons] = useState(5);


    const PageButton = ({ label, onClick, buttonType }) => (
        <button onClick={onClick} className={`page-button ${buttonType}`}>
            {label}
        </button>
    );

    const handleItemsPerPageChange = (e) => {
        const selectedValue = parseInt(e.target.value, 10);
        setItemsPerPage(selectedValue);
        setCurrentPage(1);
        if (selectedValue > 1000) {
            alert('Warning: Large number of items per page may cause performance issues.');
        }
    };

    function getFormattedTime() {
        const currentDate = new Date();
        const hours = currentDate.getHours();
        const minutes = currentDate.getMinutes();
        const seconds = currentDate.getSeconds();
        return `${hours}:${minutes}:${seconds}`;
    }

    function getFormattedDate(date) {
        if (!date) {
            date = new Date();  // Если date не передан, используем текущую дату
        }

        const day = String(date.getDate()).padStart(2, '0');
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const year = date.getFullYear();

        return `${day}.${month}.${year}`;
    }

    function getFormattedLatvianDate() {
        const daysOfWeek = ['Svētdiena', 'Pirmdiena', 'Otrdiena', 'Trešdiena', 'Ceturtdiena', 'Piektdiena', 'Sestdiena'];
        const months = ['janvāris', 'februāris', 'marts', 'aprīlis', 'maijs', 'jūnijs', 'jūlijs', 'augusts', 'septembris', 'oktobris', 'novembris', 'decembris'];

        const currentDate = new Date();
        const dayOfWeek = daysOfWeek[currentDate.getDay()];
        const dayOfMonth = currentDate.getDate();
        const month = months[currentDate.getMonth()];

        return `${dayOfWeek}, ${dayOfMonth}. ${month}`;
    }

    function updateCurrentTime() {
        setCurrentTime(getFormattedTime());
    }

    function updateCurrentDate() {
        setCurrentDate(getFormattedDate());  // Без передачи аргумента
    }

    function updateCurrentDateLV() {
        setCurrentDateLV(getFormattedLatvianDate());
    }

    useEffect(() => {
        const intervalId = setInterval(() => {
            updateCurrentTime();
            updateCurrentDateLV();
            updateCurrentDate();
        }, 1000);

        return () => clearInterval(intervalId);
    }, []);

    const romanToNumber = (roman) => {
        const romanNumeralMap = {
            I: 1,
            II: 2,
            III: 3,
            IV: 4,
            V: 5,
            VI: 6
            // Добавьте другие римские цифры при необходимости
        };

        return romanNumeralMap[roman] || 0;
    };

    const sortData = (data, columns) => {
        const sortedData = [...data];

        sortedData.sort((a, b) => {
            for (let i = 0; i < columns.length; i++) {
                const column = columns[i];
                const valueA = a[column] || '';
                const valueB = b[column] || '';

                if (column === 'class') {
                    // Разделить значение столбца 'class' на числовую и буквенную части
                    const regex = /^(\d+)([^\d]+)(?: \(([^)]+)\))?$/;
                    const matchA = valueA.match(regex);
                    const matchB = valueB.match(regex);

                    if (matchA && matchB) {
                        // Сравнить числовые части
                        const numComparison = parseInt(matchA[1], 10) - parseInt(matchB[1], 10);

                        if (numComparison !== 0) {
                            // Если числовые части не равны, вернуть результат
                            return numComparison;
                        }

                        // Если числовые части равны, сравнить римские цифры
                        const romanComparison = romanToNumber(matchA[3]) - romanToNumber(matchB[3]);

                        if (romanComparison !== 0) {
                            return romanComparison;
                        }

                        // Сравнить буквенные части без учета регистра и диакритических знаков
                        return matchA[2].localeCompare(matchB[2], 'en', { sensitivity: 'base' });
                    } else {
                        // Если не удалось разделить на числовую и буквенную части, вернуть 0
                        return 0;
                    }
                } else {
                    // Логика фильтрации для других столбцов
                    const comparison = valueA.localeCompare(valueB);

                    if (comparison !== 0) {
                        return comparison;
                    }
                }
            }
            return 0;
        });

        return sortedData;
    };


    const [spreadsheetData, setSpreadsheetData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const jsonData = await runCSharpScript();

                // Initialize variable to store current date
                let currentDate;

                // Transform the data format to match the structure expected by Data_table
                const transformedData = jsonData.map((item) => {
                    // Check if the date is present, otherwise use the current date
                    if (item['Datums']) {
                        currentDate = item['Datums'];
                    }

                    return {
                        date: currentDate,
                        subst_person: item['Aizvietotājs'],
                        lesson: item['Stunda'],
                        class: item['Klase'],
                        lesson_room: item['Kab.'],
                        graduated_teacher: item['Promesošais skolotājs'],
                        notes: item['Piezīmes'],
                    };
                });

                setSpreadsheetData(transformedData);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };


        fetchData();

        const intervalId = setInterval(() => {
            updateCurrentTime();
            updateCurrentDateLV();
            updateCurrentDate();
        }, 1000);

        return () => clearInterval(intervalId);
    }, [updateCurrentDate, updateCurrentDateLV, updateCurrentTime, setSpreadsheetData]);


    const totalPages = Math.ceil(spreadsheetData.length / itemsPerPage);

    const renderPageNumbers = () => {
        const pageNumbers = [];
        const halfButtons = Math.floor(maxPageButtons / 2);

        let startPage = currentPage - halfButtons;
        let endPage = currentPage + halfButtons;

        if (startPage <= 0) {
            startPage = 1;
            endPage = Math.min(maxPageButtons, totalPages);
        }

        if (endPage > totalPages) {
            endPage = totalPages;
            startPage = Math.max(1, totalPages - maxPageButtons + 1);
        }

        if (startPage > 1) {
            pageNumbers.push(
                <PageButton key={1} label={'<<'} onClick={() => paginate(1)} buttonType="double-arrow" />
            );

            if (startPage > 1) {
                pageNumbers.push(
                    <PageButton
                        key="left-dots"
                        label={'...'}
                        onClick={() => paginate(startPage - 1)}
                        buttonType="ellipsis"
                    />
                );
            }
        }

        for (let i = startPage; i <= endPage; i++) {
            pageNumbers.push(
                <PageButton
                    key={i}
                    label={i}
                    onClick={() => paginate(i)}
                    buttonType={i === currentPage ? "current" : "normal"}
                />
            );
        }

        if (endPage < totalPages) {
            if (endPage < totalPages - 1) {
                pageNumbers.push(
                    <PageButton
                        key="right-dots"
                        label={'...'}
                        onClick={() => paginate(endPage + 1)}
                        buttonType="ellipsis"
                    />
                );
            }
            pageNumbers.push(
                <PageButton
                    key={totalPages}
                    label={'>>'}
                    onClick={() => paginate(totalPages)}
                    buttonType="double-arrow"
                />
            );
        }

        return pageNumbers;
    };

    const paginate = (pageNumber) => setCurrentPage(pageNumber);

    const sortedFilteredData = sortData(spreadsheetData, ['subst_person', 'lesson', 'class', 'lesson_room', 'graduated_teacher', 'notes']);

    return (
        <div>
            <div>
                {currentTime}
                <br />
                {currentDateLV}
                <br />
                AIZVIETOŠANAS LAPA
                <br />
                {currentDate}
                <table className="dataTable">
                    <thead>
                        <tr>
                            <th>Aizvietotājs</th>
                            <th>Stunda</th>
                            <th>Klase</th>
                            <th>Kab.</th>
                            <th>Promešosais skolotājs</th>
                            <th>Piezīmes</th>
                        </tr>
                    </thead>
                    <tbody className="dataBody">
                        {sortedFilteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage).map((data, index) => (
                            <Data_table
                                key={index}
                                data={data}
                                currentDate={ currentDate }
                            />
                        ))}
                    </tbody>
                </table>
                <input
                    type="number"
                    id="itemsPerPage"
                    onChange={handleItemsPerPageChange}
                    placeholder="Items per page..."
                    min="1"
                    className="itemsPerPageInput"
                />

                <div className="paginationContainer">
                    {renderPageNumbers()}
                </div>
            </div>
        </div>
    );
}

export default App;
