import React, { useState, useEffect, useCallback } from 'react';
import DataTable from './Data_table';
import './styles.css';
import './App.css';

function App() {
    const [sourceList, setSourceList] = useState([]);
    const [currentTime, setCurrentTime] = useState(getFormattedTime());
    const [currentDateLV, setCurrentDateLV] = useState(getFormattedLatvianDate());
    const [currentDate, setCurrentDate] = useState(getFormattedDate());
    const [currentPage, setCurrentPage] = useState(1);
    const [spreadsheetData, setSpreadsheetData] = useState([]);
    const [itemsPerPage] = useState(20);
    const url = 'http://localhost:3001/';


    const renderContent = () => {
        if (currentPage > totalPages) {
            // Отображение фото
            const sourceIndex = currentPage - totalPages - 1;
            const source = sourceList[sourceIndex];
            const request = url + "getImage/" + source;

            if (sourceIndex >= 0 && sourceIndex < sourceList.length && source !== "URL.txt") {

                const extension = source.split(".")[1];

                if (extension !== "mp4")
                    return (
                        <img
                            src={`${request}`}
                            alt={`Sheet ${currentPage}`}
                            className="photoImage"
                        />
                    );
                else
                    return (
                        <video className="videoPlayer" autoPlay loop>
                            <source src={`${request}`} type="video/mp4" />
                            <source src={`${request}`} type="video/ogg" />
                            Your browser does not support the video tag.
                        </video>
                    );
            } else {
                if (sourceIndex > sourceList.length || sourceList.length === 0)
                    return <div>No video available for this page</div>;
                else {
                    const response = fetch("http://localhost:3001/getImage/URL.txt", {
                        "headers": {
                            "accept": "text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3;q=0.7",
                            "accept-language": "ru,en;q=0.9,en-GB;q=0.8,en-US;q=0.7",
                            "cache-control": "max-age=0",
                            "sec-ch-ua": "\"Chromium\";v=\"122\", \"Not(A:Brand\";v=\"24\", \"Microsoft Edge\";v=\"122\"",
                            "sec-ch-ua-mobile": "?0",
                            "sec-ch-ua-platform": "\"Windows\"",
                            "sec-fetch-dest": "document",
                            "sec-fetch-mode": "navigate",
                            "sec-fetch-site": "none",
                            "sec-fetch-user": "?1",
                            "upgrade-insecure-requests": "1"
                        },
                        "referrerPolicy": "strict-origin-when-cross-origin",
                        "body": null,
                        "method": "GET"
                    });
                    console.log('HTTP status:', response.status);

                    return <div>${request}</div>;
                }
            }
        } else {
            // Отображение таблицы данных
            return (
                <div>
                    <h1>{currentTime}</h1>
                    <h2>AIZVIETOŠANAS LAPA</h2>
                    <h3>{currentDateLV}</h3>
                    <div className="table-wrapper">
                        <table className="fl-table">
                            <thead>
                                <tr>
                                    <th>Aizvietotājs</th>
                                    <th>Stunda</th>
                                    <th>Klase</th>
                                    <th>Kab.</th>
                                    <th>Promešosais skolotājs</th>
                                    <th>Piezīmes</th>
                                </tr>
                            </thead>
                            <tbody className="dataBody">
                                {sortedFilteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage).map((data, index) => (
                                    <DataTable
                                        key={index}
                                        data={data}
                                        currentDate={currentDate}
                                    />
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            );
        }
    };

    useEffect(() => {
        const fetchPhotoList = async () => {
            try {
                const response = await fetch(url + 'getPhotoList');
                if (!response.ok) {
                    console.error('Failed to fetch photo list. HTTP status:', response.status);
                    throw new Error('Failed to fetch photo list');
                }
                const { photoList } = await response.json();
                setSourceList(photoList);
            } catch (error) {
                console.error('Error fetching photo list:', error);
            }
        };

        // Запускаем функцию для получения списка фото
        fetchPhotoList();
    }, []);


    const PageButton = ({ onClick, isActive }) => (
        <button onClick={onClick} className={`button-81 ${isActive ? 'active' : ''}`}>
        </button>
    );

    function getFormattedTime() {
        const currentDate = new Date();
        const hours = currentDate.getHours();
        const minutes = currentDate.getMinutes();
        const seconds = currentDate.getSeconds();
        const formattedHours = String(hours).padStart(2, '0');
        const formattedMinutes = String(minutes).padStart(2, '0');
        const formattedSeconds = String(seconds).padStart(2, '0');
        return `${formattedHours}:${formattedMinutes}:${formattedSeconds}`;
    }

    function getFormattedDate(date) {
        if (!date) {
            date = new Date();
        }

        const day = String(date.getDate()).padStart(2, '0');
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const year = date.getFullYear();

        return `${day}.${month}.${year}.`;
    }

    function getFormattedLatvianDate() {
        const daysOfWeek = ['Svētdiena', 'Pirmdiena', 'Otrdiena', 'Trešdiena', 'Ceturtdiena', 'Piektdiena', 'Sestdiena'];
        const months = ['janvāris', 'februāris', 'marts', 'aprīlis', 'maijs', 'jūnijs', 'jūlijs', 'augusts', 'septembris', 'oktobris', 'novembris', 'decembris'];

        const currentDate = new Date();
        const dayOfWeek = daysOfWeek[currentDate.getDay()];
        const dayOfMonth = currentDate.getDate();
        const month = months[currentDate.getMonth()];

        return `${dayOfWeek}, ${dayOfMonth}. ${month}`;
    }

    const updateCurrentTime = useCallback(() => {
        setCurrentTime(getFormattedTime());
    }, []);

    const updateCurrentDate = useCallback(() => {
        setCurrentDate(getFormattedDate());
    }, []);

    const updateCurrentDateLV = useCallback(() => {
        setCurrentDateLV(getFormattedLatvianDate());
    }, []);

    useEffect(() => {
        const intervalId = setInterval(() => {
            updateCurrentTime();
            updateCurrentDateLV();
            updateCurrentDate();
        }, 1000);

        return () => clearInterval(intervalId);
    }, [updateCurrentDate, updateCurrentDateLV, updateCurrentTime]);

    const romanToNumber = (roman) => {
        const romanNumeralMap = {
            I: 1,
            II: 2,
            III: 3
        };

        return romanNumeralMap[roman] || 0;
    };

    const sortData = (data, columns) => {
        const sortedData = [...data];

        sortedData.sort((a, b) => {
            for (let i = 0; i < columns.length; i++) {
                const column = columns[i];
                const valueA = a[column] || '';
                const valueB = b[column] || '';

                if (column === 'class') {
                    const regex = /^(\d+)([^\d]+)(?: \(([^)]+)\))?$/;
                    const matchA = valueA.match(regex);
                    const matchB = valueB.match(regex);

                    if (matchA && matchB) {
                        const numComparison = parseInt(matchA[1], 10) - parseInt(matchB[1], 10);

                        if (numComparison !== 0) {
                            return numComparison;
                        }

                        const romanComparison = romanToNumber(matchA[3]) - romanToNumber(matchB[3]);

                        if (romanComparison !== 0) {
                            return romanComparison;
                        }

                        return matchA[2].localeCompare(matchB[2], 'en', { sensitivity: 'base' });
                    } else {
                        return 0;
                    }
                } else {
                    const comparison = valueA.localeCompare(valueB, 'en', { sensitivity: 'base' });

                    if (comparison !== 0) {
                        return comparison;
                    }
                }
            }
            return 0;
        });

        return sortedData;
    };



    useEffect(() => {
        const runCSharpScript = async () => {
            try {
                console.log('Sending a POST request to run C# script...');

                const response = await fetch(url + 'runCSharpScript', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });

                console.log('Server response received:', response);

                if (!response.ok) {
                    console.error('Failed to run C# script on the server. HTTP status:', response.status);
                    throw new Error('Failed to run C# script on the server');
                }

                const JsonData = await response.text(); // Get the raw text response

                const jsonData = JSON.parse(JsonData); // Parse the cleaned JSON string

                let dataArray = JSON.parse(jsonData.data);

                // Заполняем пропуски в свойстве "date"
                for (let i = 0; i < dataArray.length; i++) {
                    if (dataArray[i].date === null || dataArray[i].date === "") {
                        dataArray[i].date = dataArray[i - 1].date;
                    }
                }

                // Удаляем элементы, у которых все свойства (кроме "date") пусты
                dataArray = dataArray.filter(item => {
                    if (item.subst_person === "" || item.subst_person === "nav stundas")
                        return false; // Все свойства (кроме "date") пусты, удаляем элемент
                    return true;
                });

                const filteredData = filterDataByCurrentDate(dataArray, currentDate);

                console.log('C# script executed successfully. Data from the server:', filteredData);
                setSpreadsheetData(filteredData);
            } catch (error) {
                console.error('Error running C# script:', error);
            }
        };


        // Вызываем функцию для отправки POST-запроса
        runCSharpScript();

        const intervalId = setInterval(() => {
            updateCurrentTime();
            updateCurrentDateLV();
            updateCurrentDate();
        }, 1000);

        return () => clearInterval(intervalId);
    }, [updateCurrentDate, updateCurrentDateLV, updateCurrentTime, setSpreadsheetData, currentDate]);


    const totalPages = Math.ceil(spreadsheetData.length / itemsPerPage);

    const renderPageNumbers = () => {
        const pageNumbers = [];

        for (let i = 1; i <= totalPages + sourceList.length; i++) {
            if (sourceList[i - totalPages - 1] !== "logo.png") {
                pageNumbers.push(
                    <PageButton
                        key={i}
                        onClick={() => paginate(i)}
                        isActive={i === currentPage}
                    />
                );
            }
        }

        return pageNumbers;
    };


    const paginate = (pageNumber) => {
        setCurrentPage(pageNumber);
        if (pageNumber > totalPages)
            console.log(`Clicked on photo page ${pageNumber}`);
    };

    const filterDataByCurrentDate = (data, currentDate) => {
        return data.filter(item => {
            const itemDate = item.date;
            return itemDate === currentDate;
        });
    };

    const sortedFilteredData = sortData(spreadsheetData, ['subst_person', 'lesson', 'class', 'lesson_room', 'graduated_teacher', 'notes']);

    return (
        <div>


            {renderContent()}
            {/*
                    <input
                        type="number"
                        id="itemsPerPage"
                        onChange={handleItemsPerPageChange}
                        placeholder="Items per page..."
                        min="1"
                        className="itemsPerPageInput"
                    />
                */}
            <div className="paginationContainer">
                {renderPageNumbers()}
            </div>
        </div>
    );
}

