    };

    const getFolderName = (folder) => {
        // Разделяем имя файла по точке
        const parts = folder.split('.');
        // Удаляем последнюю часть (расширение файла)
        parts.pop();
        // Соединяем оставшиеся части снова в строку
        return parts.join('.');
    };

