        let newFolderName = prompt("Ievadiet jaunu mapes nosaukumu:", getFolderName(folderName)); // Предполагается, что у вас есть доступ к названию папки
        if (!newFolderName.endsWith(".txt")) {
            newFolderName += ".txt";
        }

        if (newFolderName !== null) {
            fetch(`${url}renameFolder/${encodeURIComponent(folderName)}`, { // Передаем название папки в параметре URL
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ newFileName: newFolderName }) // Передаем новое название папки в теле запроса
            })
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Kļūda, mainot mapes nosaukumu');
                    }
                    return response.json();
                })
                .then(data => {
                    alert(data.message); // Показываем сообщение об успешном изменении названия
                    // Можно выполнить дополнительные действия после успешного изменения названия папки
                    const updatedFolders = folders.map(folderItem => {
