import React, { useState } from 'react';
import * as XLSX from 'xlsx';

const AdminConsole = () => {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [loggedInData, setLoggedInData] = useState(null);
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = async () => {
        // Отправляем запрос на сервер для проверки логина и пароля
        try {
            const response = await fetch('http://localhost:3001/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ login, password }),
            });

            if (!response.ok) {
                console.error('Authentication failed. HTTP status:', response.status);
                // Обработка ошибки аутентификации
                return;
            }

            const data = await response.json();
            setLoggedInData(data);
            setIsLoggedIn(true);
        } catch (error) {
            console.error('Error during login:', error);
        }
    };

    const handleRunCSharpScript = async () => {
        try {
            const response = await fetch('http://localhost:3001/runCSharpScript', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                console.error('Failed to run C# script on the server. HTTP status:', response.status);
                throw new Error('Failed to run C# script on the server');
            }

            const jsonData = await response.json();

            if (!jsonData.data.trim()) {
                console.error('Empty response data received from the server.');
                return;
            }

            console.log('Raw server response data:', jsonData.data);

            let dataArray;

            try {
                dataArray = JSON.parse(jsonData.data);
            } catch (parseError) {
                console.error('Error parsing JSON data:', parseError);
                return;
            }

            if (!Array.isArray(dataArray)) {
                console.error('Data is not an array:', dataArray);
                return;
            }

            console.log('Parsed server response data:', dataArray);

            const ws = XLSX.utils.json_to_sheet(dataArray);
            const wb = XLSX.utils.book_new();
            XLSX.utils.book_append_sheet(wb, ws, 'Sheet1');

            XLSX.writeFile(wb, 'data.xlsx');
        } catch (error) {
            console.error('Error during C# script execution:', error);
        }
    };

    const handleChooseColumns = () => {
        const picker = new google.picker.PickerBuilder()
            .addView(new google.picker.DocsView(google.picker.ViewId.SPREADSHEETS))
            .setOAuthToken(loggedInData.accessToken)
            .setCallback((data) => {
                if (data.action === google.picker.Action.PICKED) {
                    const doc = data.docs[0];
                    const spreadsheetId = doc.id;

                    // Prompt user for column selection (mocked with prompts)
                    const columns = [
                        prompt('Select Datums column:'),
                        prompt('Select Aizvietotājs column:'),
                        prompt('Select Stunda column:'),
                        prompt('Select Klase column:'),
                        prompt('Select Kab. column:'),
                        prompt('Select Promesošais skolotājs column:'),
                        prompt('Select Piezīmes column:'),
                    ];

                    if (spreadsheetId && columns.every(column => column !== null && column !== undefined)) {
                        console.log('Selected Spreadsheet ID:', spreadsheetId);
                        console.log('Selected columns:', columns);
                    }
                }
            })
            .build();

        picker.setVisible(true);
    };

    return (
        <div>
            {!isLoggedIn && (
                <>
                    <div>
                        <label htmlFor="login">Login:</label>
                        <input
                            type="text"
                            id="login"
                            value={login}
                            onChange={(e) => setLogin(e.target.value)}
                        />
                    </div>
                    <div>
                        <label htmlFor="password">Password:</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <button onClick={handleLogin}>Enter</button>
                </>
            )}

            {isLoggedIn && (
                <div>
                    {/* Button to run C# script */}
                    <button onClick={handleRunCSharpScript}>Run C# Script</button>

                    {/* Button to choose columns */}
                    <button onClick={handleChooseColumns}>Choose Columns</button>
                </div>
            )}
        </div>
    );
};

export default AdminConsole;
