// adminToServer.js

import axios from 'axios';

const url = 'http://localhost:3001/'; // �������� �� ��� URL �������, ���� �����

export const updateConfig = async (updates) => {
    try {
        const response = await fetch(url + 'updateConfig', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(updates)
        });
        return await response.json();
    } catch (error) {
        console.error('Error updating config:', error);
        throw error;
    }
};

export const getAllFiles = async () => {
    try {
        const response = await fetch(url + 'getAllFiles');
        return await response.json();
    } catch (error) {
        console.error('Error getting folders:', error);
        throw error;
    }
};

export const addSourceFile = async (fileName) => {
    try {
        const response = await fetch(url + 'addSourceFile', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ fileName }),
        });
        return await response.json();
    } catch (error) {
        console.error('Error adding folder:', error);
        throw error;
    }
};

export const deleteSourceFile = async (fileName) => {
    try {
        const response = await fetch(url + `deleteSourceFile/${fileName}`, {
            method: 'DELETE',
        });
        return await response.json();
    } catch (error) {
        console.error('Error deleting folder:', error);
        throw error;
    }
};

export const deleteSource = async (selectedFolder, type, fileName) => {
    try {
        const response = await fetch(url + `deleteSource/${selectedFolder}/${type}/${fileName}`, {
            method: 'DELETE',
        });
        return response.ok;
    } catch (error) {
        console.error('Error deleting file:', error);
        throw error;
    }
};

export const getSourcesList = async (fileName) => {
    try {
        const response = await fetch(url + `getSourcesList/${fileName}`);
        return await response.json();
    } catch (error) {
        console.error('Error getting sources list:', error);
        throw error;
    }
};

export const logIn = async (login, password, token) => {
    try {
        const response = await fetch(url + 'login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                login: login,
                password: password,
                recaptchaToken: token
            }),
        });

        const data = await response.json();

        if (response.ok) {
            return { isLoggedIn: true, data };
        } else {
            return { isLoggedIn: false, error: response.statusText };
        }
    } catch (error) {
        return { isLoggedIn: false, error };
    }
};

export const changeLogin = async (oldLogin, newLogin, password) => {
    try {
        const response = await fetch(url + 'changelogin', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ oldLogin, newLogin, password }),
        });

        if (response.ok) {
            return { success: true };
        } else {
            return { success: false, error: 'Failed to update login' };
        }
    } catch (error) {
        return { success: false, error: 'Network error. Failed to update login' };
    }
};

export const runCSharpScript = async () => {
    try {
        const response = await fetch(url + 'runCSharpScript', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
        });

        if (!response.ok) {
            throw new Error(`Failed to run C# script on the server. HTTP status: ${response.status}`);
        }

        const jsonData = await response.json();

        if (!jsonData.data.trim()) {
            console.error('Empty response data received from the server.');
            return;
        }

        let dataArray;

        try {
            dataArray = JSON.parse(jsonData.data);
        } catch (parseError) {
            throw new Error(`Error parsing JSON data: ${parseError}`);
        }

        if (!Array.isArray(dataArray)) {
            throw new Error(`Data is not an array: ${dataArray}`);
        }

        return dataArray;
    } catch (error) {
        throw new Error(`Error during C# script execution: ${error}`);
    }
};
