import React, { useRef, useState, useEffect, useCallback } from 'react';
import { v4 as uuidv4 } from 'uuid';
import {
    DndContext,
    DragEndEvent,
    DragMoveEvent,
    DragOverlay,
    DragStartEvent,
    KeyboardSensor,
    PointerSensor,
    closestCorners,
    useSensor,
    useSensors,
} from '@dnd-kit/core';
import { UniqueIdentifier } from '@dnd-kit/utilities';
import {
    SortableContext,
    arrayMove,
    sortableKeyboardCoordinates,
} from '@dnd-kit/sortable';
import ReCAPTCHA from "react-google-recaptcha";
import ReactPlayer from 'react-player';
import moment from 'moment-timezone';
import { ReactAnglePicker } from 'react-angle-picker';
import './style_adm.css';
import { updateConfig, getAllFiles, addSourceFile, deleteSourceFile, deleteSource, getSourcesList, authenticate, changeLogin, runCSharpScript, getConfig, getCSharpScriptConfigAsnc, updateStyles, applyTableStylesAsync } from './adminToServer.js';
import * as XLSX from 'xlsx';

const AdminConsole = () => {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [loginInfo,setLoginInfo] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [excelData, setExcelData] = useState(null);
    const [selectedCell, setSelectedCell] = useState({ row: null, col: null });
    const [config, setConfig] = useState({});
    const [newConfig, setNewConfig] = useState({});
    const [newCSConfig, setNewCSConfig] = useState({});
    const [csharpScriptConfig, setCSharpScriptConfig] = useState({});
    const url = 'http://localhost:3001/';
    const [activeTab, setActiveTab] = useState('aizvietosanas');
    const [selectedOption, setSelectedOption] = useState('Vienlaidu');
    const [color, setColor] = useState('#ff0000');
    const [color1, setColor1] = useState('#ff0000');
    const [color2, setColor2] = useState('#00ff00');
    const [angle, setAngle] = React.useState(0);
    const [tableColor, setTableColor] = useState('');
    const [backgroundType, setBackgroundType] = useState('');
    const [headerStyles, setHeaderStyles] = useState({
        fontSize: '',
        fontFamily: '',
        fontWeight: '',
        fontStyle: '',
        textTransform: '',
        color: ''
    });
    const [cellStyles, setCellStyles] = useState({
        fontSize: '',
        fontFamily: '',
        fontWeight: '',
        fontStyle: '',
        textTransform: '',
        color: ''
    });
    const fontFamilies = [
        'Arial',
        'Verdana',
        'Tahoma',
        'Times New Roman',
        'Georgia',
        'Courier New',
        // Добавьте другие варианты семейств шрифтов по желанию
    ];
    const units = ['px', '%', 'vw', 'vh'];
    const [folders, setFolders] = useState([]);
    const [selectedFolder, setSelectedFolder] = useState('');
    const [sourcesList, setSourcesList] = useState([]);
    const [oldPassword, setOldPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmNewPassword, setConfirmNewPassword] = useState('');
    const [oldLogin, setOldLogin] = useState('');
    const [newLogin, setNewLogin] = useState('');
    const [selectedTimeZone, setSelectedTimeZone] = useState('Europe/Riga'); // Установите часовой пояс по умолчанию
    const timeZones = moment.tz.names();
    const [spreadsheetUrl, setSpreadsheetUrl] = useState('');
    const [credentialFile, setCredentialFile] = useState(null);
    const [columnNamesRange, setColumnNamesRange] = useState('');
    const [dataRange, setDataRange] = useState('');
    const reRef = useRef();
    const key = "6LcPG6EpAAAAADbUuzfmtz2P_oTIvj6ydJ6F6zms";
    const [serverFiles, setServerFiles] = useState([]);
    const [containers, setContainers] = useState([
        {
            id: 1,
            title: 'serverFiles - все файлы сервера',
            items: []
        },
        {
            id: 2,
            title: 'sourcesList - файлы конкретной папки',
            items: []
        }
    ]);
    const [activeId, setActiveId] = useState < UniqueIdentifier | null > (null);
    const [itemName, setItemName] = useState('');

    const onAddItem = (containerId: number) => {
        if (!itemName) return;
        const id = uuidv4();
        const containerIndex = containers.findIndex(container => container.id === containerId);
        if (containerIndex === -1) return;
        const updatedContainers = [...containers];
        updatedContainers[containerIndex].items.push({
            id,
            type: '',
            file: itemName,
            index: updatedContainers[containerIndex].items.length
        });
        setContainers(updatedContainers);
        setItemName('');
    };

    // DND Handlers
    const sensors = useSensors(
        useSensor(PointerSensor),
        useSensor(KeyboardSensor, {
            coordinateGetter: sortableKeyboardCoordinates,
        }),
    );

    function handleDragStart(event: DragStartEvent) {
        const { active } = event;
        const { id } = active;
        setActiveId(id);
    }

    const handleDragMove = (event: DragMoveEvent) => {
        // Handle drag move if needed
    };

    function handleDragEnd(event: DragEndEvent) {
        // Handle drag end if needed
        setActiveId(null);
    }

    const handleTableColorChangeDebounced = useCallback(debounce((newColor) => {
        setHeaderStyles({ ...headerStyles, color: newColor });
        setCellStyles({ ...cellStyles, color: newColor });
        setTableColor(newColor);
    }, 300), [headerStyles, cellStyles]);

    useEffect(() => {
        const fetchSources = async () => {
            try {
                const response = await fetch(url + 'getAllSources');
                if (!response.ok) {
                    throw new Error('Failed to fetch sources');
                }
                const data = await response.json();
                const filesWithMetadata = data.files.map((file, index) => ({
                    id: index,
                    type: getFileType(file),
                    file,
                    index: index
                }));
                setServerFiles(filesWithMetadata);
                console.log("All files: " + filesWithMetadata);
            } catch (error) {
                console.error('Error fetching sources:', error);
            }
        };
        fetchSources();
    }, []);

    const handleAddURL = async () => {
        let urlInput = prompt('Enter URL:');
        if (urlInput) {
            // Проверяем, является ли введенное значение ссылкой
            if (isValidURL(urlInput)) {
                // Проверяем, является ли ссылка видео или фото
                const fileType = await getFileTypeFromURL(urlInput);

                // Если тип файла определен, делаем запрос на сервер
                if (fileType) {
                    try {
                        const response = await fetch(`${url}addSource/${encodeURIComponent(selectedFolder)}/${fileType}/${encodeURIComponent(urlInput)}`, {
                            method: 'POST',
                        });
                        if (response.ok) {
                            console.log('File added successfully');
                        } else {
                            console.error('Failed to add file');
                        }
                    } catch (error) {
                        console.error('Error adding file:', error);
                    }
                } else {
                    console.error('Unsupported file type');
                }
            } else {
                console.error('Invalid URL');
            }
        } else {
            console.error('URL is empty');
        }
    };

    // Функция для определения, является ли строка URL
    function isValidURL(string) {
        try {
            new URL(string);
            return true;
        } catch (_) {
            return false;
        }
    }


    // Функция для определения типа файла по URL
    const getFileTypeFromURL = async (url) => {
        try {
            const response = await fetch(url);
            const contentType = response.headers.get('content-type');
            if (url.includes('youtu.be') || url.includes('youtube.com')) {
                return 'URL(video)';
            } else if (contentType && contentType.includes('image')) {
                return 'URL(foto)';
            } else if (contentType && contentType.includes('video')) {
                return 'URL(video)';
            } else {
                return null; // Неизвестный тип файла
            }
        } catch (error) {
            console.error('Error fetching URL:', error);
            return null;
        }
    };

    const handleFileInputChange = async (event) => {
        if (event.target.files) { // Проверяем, что есть выбранный файл
            const file = event.target.files[0];
            if (!file) return;

            // Определяем тип файла на основе его расширения
            const fileType = getFileType(file.name);

            // Если тип файла определен, делаем запрос на сервер
            if (fileType) {
                const formData = new FormData();
                formData.append('file', file);
                formData.append('type', fileType);
                formData.append('folder', selectedFolder);

                try {
                    const response = await fetch(`${url}addSource/${encodeURIComponent(selectedFolder)}/${fileType}/${encodeURIComponent(file.name)}`, {
                        method: 'POST',
                        body: formData
                    });
                    if (response.ok) {
                        console.log('File added successfully');
                    } else {
                        console.error('Failed to add file');
                    }
                } catch (error) {
                    console.error('Error adding file:', error);
                }
            } else {
                console.error('Unsupported file type');
            }
        }
    };

    // Функция для определения типа файла на основе его расширения
    const getFileType = (fileName) => {
        const extension = fileName.split('.').pop();
        switch (extension.toLowerCase()) {
            case 'jpg':
            case 'jpeg':
            case 'png':
            case 'gif':
                return 'foto';
            case 'mp4':
                return 'video';
            case 'txt':
                return 'text';
            default:
                return null; // Неизвестный тип файла
        }
    };

    const handleColumnNamesRangeChange = (range) => {
        if (isValidRange(range))
            setColumnNamesRange(range);
        else
            console.log("Invalid");
    };

    const isValidCellCharacter = (charCode) => {
        // Допустимыми символами являются заглавные латинские буквы
        return (charCode >= 65 && charCode <= 90); // A-Z
    };

    const isValidRange = (range) => {
        // Регулярное выражение для проверки формата ренджа Excel (например, "A1:B10")
        const excelRangeRegex = /^[A-Za-z]+\d+:[A-Za-z]+\d+$/;
        if (!excelRangeRegex.test(range)) {
            return false; // Неверный формат ренджа
        }

        // Разделить рендж на две ячейки: начальную и конечную
        const [startCell, endCell] = range.split(':');

        // Проверка корректности номеров столбцов
        const columnRegex = /^[A-Za-z]+$/;
        if (!columnRegex.test(startCell.match(/[A-Za-z]+/)[0]) || !columnRegex.test(endCell.match(/[A-Za-z]+/)[0])) {
            return false; // Некорректное обозначение столбцов
        }

        // Проверка корректности номеров строк
        const rowRegex = /^\d+$/;
        if (!rowRegex.test(startCell.match(/\d+/)[0]) || !rowRegex.test(endCell.match(/\d+/)[0])) {
            return false; // Некорректное обозначение строк
        }

        return true; // Рендж соответствует формату Excel
    };

    const handleDataRangeChange = (range) => {
        setDataRange(range);
    };

