import React, { useRef, useState, useEffect, useCallback } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import ReCAPTCHA from "react-google-recaptcha";
import moment from 'moment-timezone';
import { ReactAnglePicker } from 'react-angle-picker';
import './style_adm.css';
import { getFilesInThisFolder, renameFolder, getFileDisplay, isValidURL, getFileTypeFromURL, getFileType, updateConfig, getAllFileFolders, addSourceFolder, deleteSourceFolder, deleteSource, getSourcesList, authenticate, changeLogin, runCSharpScript, getConfig, getCSharpScriptConfigAsnc, updateStyles, applyTableStylesAsync } from './adminToServer.js';
import * as XLSX from 'xlsx';

const AdminConsole = () => {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [loginInfo,setLoginInfo] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [excelData, setExcelData] = useState(null);
    const [selectedCell, setSelectedCell] = useState({ row: null, col: null });
    const [config, setConfig] = useState({});
    const [newConfig, setNewConfig] = useState({});
    const [newCSConfig, setNewCSConfig] = useState({});
    const [csharpScriptConfig, setCSharpScriptConfig] = useState({});
    const url = 'http://localhost:3001/';
    const [activeTab, setActiveTab] = useState('aizvietosanas');
    const [selectedOption, setSelectedOption] = useState('Vienlaidu');
    const [color, setColor] = useState('#ff0000');
    const [color1, setColor1] = useState('#ff0000');
    const [color2, setColor2] = useState('#00ff00');
    const [angle, setAngle] = React.useState(0);
    const [tableColor, setTableColor] = useState('');
    const [backgroundType, setBackgroundType] = useState('');
    const [headerStyles, setHeaderStyles] = useState({
        fontSize: '',
        fontFamily: '',
        fontWeight: '',
        fontStyle: '',
        textTransform: '',
        color: ''
    });
    const [cellStyles, setCellStyles] = useState({
        fontSize: '',
        fontFamily: '',
        fontWeight: '',
        fontStyle: '',
        textTransform: '',
        color: ''
    });
    const fontFamilies = [
        'Arial',
        'Verdana',
        'Tahoma',
        'Times New Roman',
        'Georgia',
        'Courier New',
        // Добавьте другие варианты семейств шрифтов по желанию
    ];
    const units = ['px', '%', 'vw', 'vh'];
    const [folders, setFolders] = useState([]);
    const [selectedFolder, setSelectedFolder] = useState('');
    const [sourcesList, setSourcesList] = useState([]);
    const [oldPassword, setOldPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmNewPassword, setConfirmNewPassword] = useState('');
    const [oldLogin, setOldLogin] = useState('');
    const [newLogin, setNewLogin] = useState('');
    const [selectedTimeZone, setSelectedTimeZone] = useState('Europe/Riga'); // Установите часовой пояс по умолчанию
    const timeZones = moment.tz.names();
    const [spreadsheetUrl, setSpreadsheetUrl] = useState('');
    const [credentialFile, setCredentialFile] = useState(null);
    const [columnNamesRange, setColumnNamesRange] = useState('');
    const [dataRange, setDataRange] = useState('');
    const reRef = useRef();
    const key = "6LcPG6EpAAAAADbUuzfmtz2P_oTIvj6ydJ6F6zms";

    const handleTableColorChangeDebounced = useCallback(debounce((newColor) => {
        setHeaderStyles({ ...headerStyles, color: newColor });
        setCellStyles({ ...cellStyles, color: newColor });
        setTableColor(newColor);
    }, 300), [headerStyles, cellStyles]);

    const handleAddURL = async () => {
        let urlInput = prompt('Enter URL:');
        if (urlInput) {
            // Проверяем, является ли введенное значение ссылкой
            if (isValidURL(urlInput)) {
                // Проверяем, является ли ссылка видео или фото
                const fileType = await getFileTypeFromURL(urlInput);

                // Если тип файла определен, делаем запрос на сервер
                if (fileType) {
                    try {
                        const response = await fetch(`${url}addSource/${encodeURIComponent(selectedFolder)}/${fileType}/${encodeURIComponent(urlInput)}`, {
                            method: 'POST',
                        });
                        if (response.ok) {
                            console.log('File added successfully');
                        } else {
                            console.error('Failed to add file');
                        }
                    } catch (error) {
                        console.error('Error adding file:', error);
                    }
                } else {
                    console.error('Unsupported file type');
                }
            } else {
                console.error('Invalid URL');
            }
        } else {
            console.error('URL is empty');
        }
    };

    function handleFileInputChange(event) {
        const file = event.target.files[0]; // Получаем первый выбранный файл (может быть только один)

        if (file) {
            const formData = new FormData();
            formData.append('file', file); // Добавляем файл в объект FormData

            const folderName = selectedFolder || ''; // Вставьте здесь реальное название папки
            const status = 'ok'; // Вставьте здесь нужный статус (ok, replace, rename, keep)

            fetch(`/addSourceFile/${folderName}`, {
                method: 'POST',
                body: formData,
                headers: {
                    'status': status
                }
            })
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Ошибка при загрузке файла');
                    }
                    return response.json(); // Возвращаем JSON-ответ сервера
                })
                .then(data => {
                    console.log('Файл успешно загружен:', data);
                    // Здесь вы можете обработать успешный ответ сервера
                })
                .catch(error => {
                    console.error('Ошибка при загрузке файла:', error);
                    // Здесь вы можете обработать ошибку загрузки файла
                });
        } else {
            console.log('Файл не выбран');
        }
    }


    const handleColumnNamesRangeChange = (range) => {
        if (isValidRange(range))
            setColumnNamesRange(range);
        else
            console.log("Invalid");
    };

    const isValidCellCharacter = (charCode) => {
        // Допустимыми символами являются заглавные латинские буквы
        return (charCode >= 65 && charCode <= 90); // A-Z
    };

    const isValidRange = (range) => {
        // Регулярное выражение для проверки формата ренджа Excel (например, "A1:B10")
        const excelRangeRegex = /^[A-Za-z]+\d+:[A-Za-z]+\d+$/;
        if (!excelRangeRegex.test(range)) {
            return false; // Неверный формат ренджа
        }

        // Разделить рендж на две ячейки: начальную и конечную
        const [startCell, endCell] = range.split(':');

        // Проверка корректности номеров столбцов
        const columnRegex = /^[A-Za-z]+$/;
        if (!columnRegex.test(startCell.match(/[A-Za-z]+/)[0]) || !columnRegex.test(endCell.match(/[A-Za-z]+/)[0])) {
            return false; // Некорректное обозначение столбцов
        }

        // Проверка корректности номеров строк
        const rowRegex = /^\d+$/;
        if (!rowRegex.test(startCell.match(/\d+/)[0]) || !rowRegex.test(endCell.match(/\d+/)[0])) {
            return false; // Некорректное обозначение строк
        }

        return true; // Рендж соответствует формату Excel
    };

    const handleDataRangeChange = (range) => {
        setDataRange(range);
    };

    const handleTimeZoneChange = async (e) => {
        setSelectedTimeZone(e.target.value);
        const updates = [
            { key: 'TimeZone', value: e.target.value },
            // Другие обновления конфигурации, если есть
        ];

        try {
            const data = await updateConfig(updates);
            console.log(data); // Вывод ответа от сервера
        } catch (error) {
            console.error('Error updating config:', error);
        }
    };


    useEffect(() => {
        getAllFolders();
    }, []);
    const getAllFolders = async () => {
        try {
            const data = await getAllFileFolders();
            setFolders(data);
        } catch (error) {
            console.error('Error getting folders:', error);
        }
    };
    const handleAddFolder = async () => {
        let folderName = prompt('Enter folder name:');
        if (folderName) {

            try {
                await addSourceFolder(folderName);
                getAllFolders();
            } catch (error) {
                console.error('Error adding folder:', error);
            }
        }
    };
    const handleFolderClick = async (folderName) => {
        setSelectedFolder(folderName);
        console.log('Getting sources list:', folderName);
        try {
            const data = await getFilesInThisFolder(folderName);
            const sourcesList = data;
            setSourcesList(sourcesList);
            console.log('Got sources list:', sourcesList);
        } catch (error) {
            console.error('Error getting sources list:', error);
        }
    };
    const handleRenameFolder = async (folderName) => {
        let newFolderName = prompt("Ievadiet jaunu mapes nosaukumu:", folderName);

        if (newFolderName !== null) {
            try {
                const data = await renameFolder(folderName, newFolderName); // Используем функцию для отправки запроса к серверу
                alert(data.message);
                getAllFolders();
            } catch (error) {
                console.error('Kļūda, mainot mapes nosaukumu:', error);
                alert('Radās kļūda, mainot mapes nosaukumu');
            }
        }
    };
    const handleDeleteFolder = async (folderName) => {
        if (window.confirm('Are you sure you want to delete this folder?')) {
            try {
                await deleteSourceFolder(folderName);
                getAllFolders();
            } catch (error) {
                console.error('Error deleting folder:', error);
            }
        }
    };


    const handleDeleteLink = async (type, fileName) => {
        try {
            const success = await deleteSource(selectedFolder, type, fileName);
            if (success) {
                console.log('File deleted successfully');
                // Здесь можно добавить дополнительные действия после успешного удаления файла, если необходимо
                setSourcesList(prevList => prevList.filter(item => item.type !== type || item.fileName !== fileName));
            } else {
                console.error('Error deleting file: Failed to delete file');
                // Обработка ошибок удаления файла
            }
        } catch (error) {
            console.error('Error deleting file:', error);
            // Обработка ошибок сетевого запроса
        }
    };

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    const handleLogin = async () => {
        try {
            // Получаем токен ReCAPTCHA
            const token = await reRef.current.executeAsync();
            reRef.current.reset();

            // Аутентификация
            const response = await authenticate(login, password, token);

            // Обрабатываем ответ сервера
            if (response.ok) {
                setIsLoggedIn(true);
            } else {
                // Обработка ошибки входа
                console.error('Login failed');
                setLoginInfo(response.statusText);
            }
        } catch (error) {
            console.error('Error:', error);
            setLoginInfo(error);
        }
    };

    const handleLoginChange = async () => {
        if (oldLogin === '' || newLogin === '' || password === '') {
            alert('Old login, new login, and new password are required.');
            return;
        }

        try {
            const { success, error } = await changeLogin(oldLogin, newLogin, password);
            if (success) {
                alert('Login updated successfully.');
            } else {
                alert(error);
            }
        } catch (error) {
            console.error('Error updating login:', error);
            alert('Network error. Failed to update login.');
        }
    };

    const handleRunCSharpScript = async () => {
        try {
            const dataArray = await runCSharpScript();
            if (dataArray) {
                const ws = XLSX.utils.json_to_sheet(dataArray);
                const wb = XLSX.utils.book_new();
                XLSX.utils.book_append_sheet(wb, ws, 'Sheet1');
                XLSX.writeFile(wb, 'data.xlsx');
            }
        } catch (error) {
            console.error('Error during C# script execution:', error);
        }
    };

    useEffect(() => {
        // Вызывается при монтировании компонента
        getConfig(); // Получаем конфигурацию при загрузке
        handleGetCSharpScriptConfig();
    }, []);

    const handleGetConfig = async () => {
        try {
            const config = await getConfig();
            setConfig(config);
        } catch (error) {
            console.error('Error getting configuration:', error);
        }
    };

    const handleGetCSharpScriptConfig = async () => {
        try {
            const cSharpScriptConfig = await getCSharpScriptConfigAsnc();
            setCSharpScriptConfig(cSharpScriptConfig);
        } catch (error) {
            console.error('Error getting C# Script configuration:', error);
        }
    };

    const handleOptionChange = (option) => {
        setSelectedOption(option);
    };

    const handleColorChangeDebounced = debounce((newColor) => {
        setColor(newColor);
    }, 300);

    // Вспомогательная функция debounce
    function debounce(func, timeout) {
        let timer;
        return function (...args) {
            clearTimeout(timer);
            timer = setTimeout(() => func.apply(this, args), timeout);
        };
    }

    // Обработчик изменения цвета
    const handleColorChange = (e) => {
        const newColor = e.target.value;
        handleColorChangeDebounced(newColor);
    };

    const handleChange = (value) => {
        setAngle(value);
    };

    const handleColorChange1Debounced = debounce((newColor) => {
        setColor1(newColor);
    }, 300);

    // Обработчик изменения цвета 2 с задержкой
    const handleColorChange2Debounced = debounce((newColor) => {
        setColor2(newColor);
    }, 300);

    // Вспомогательная функция debounce
    function debounce(func, timeout) {
        let timer;
        return function (...args) {
            clearTimeout(timer);
            timer = setTimeout(() => func.apply(this, args), timeout);
        };
    }

    // Обработчик изменения цвета 1
    const handleColorChange1 = (e) => {
        const newColor = e.target.value;
        handleColorChange1Debounced(newColor);
    };

    // Обработчик изменения цвета 2
    const handleColorChange2 = (e) => {
        const newColor = e.target.value;
        handleColorChange2Debounced(newColor);
    };

    const applySingleColor = async () => {
        const bodyData = { color };
        try {
            const data = await updateStyles(bodyData);
            console.log('Response from updateStyles:', data);
            // Дополнительная обработка данных, если необходимо
        } catch (error) {
            console.error('Error applying single color:', error);
        }
    };

    const applyGradient = async () => {
        const bodyData = { color1, color2, angle };
        try {
            const data = await updateStyles(bodyData);
            console.log('Response from updateStyles:', data);
            // Дополнительная обработка данных, если необходимо
        } catch (error) {
            console.error('Error applying gradient:', error);
        }
    };

    const resetToDefaultGradient = async () => {
        const defaultGradient = 'linear-gradient(229.82deg, #FFC400 18.72%, #FF6B00 84.82%)';
        const bodyData = { defaultGradient };
        try {
            const data = await updateStyles(bodyData);
            console.log('Response from updateStyles:', data);
            // Дополнительная обработка данных, если необходимо
        } catch (error) {
            console.error('Error resetting to default gradient:', error);
        }
    };

    const applyTableStyles = async () => {
        try {
            const data = await applyTableStylesAsync(headerStyles, cellStyles);
            console.log(data);
        } catch (error) {
            console.error('Error applying table styles:', error);
        }
    };

    const handleHeaderFontSizeChange = (value, type) => {
        if (value !== '')
            setHeaderStyles({ ...headerStyles, fontSize: value + type });
    };

    const handleCellFontSizeChange = (value, type) => {
        if (value !== '')
            setCellStyles({ ...cellStyles, fontSize: value + type });
    };

    const handlePasswordChange = async () => {
        // Проверка на совпадение нового пароля и его подтверждения
        if (newPassword !== confirmNewPassword) {
            alert('New password and confirm password must match');
            return;
        }

        try {
            const response = await fetch(url + 'encryptpassword', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    password: newPassword,
                    oldPassword: oldPassword
                })
            });

            if (!response.ok) {
                throw new Error('Failed to update password');
            }

            // Очистка полей после успешного обновления пароля
            setOldPassword('');
            setNewPassword('');
            setConfirmNewPassword('');

            // Дополнительные действия после успешного обновления пароля, например, показать сообщение об успехе
            alert('Password updated successfully');
        } catch (error) {
            console.error('Error updating password:', error.message);
            // Действия в случае ошибки, например, показать сообщение об ошибке
            alert('Failed to update password. Please try again later.');
        }
    };

    const sortedTimeZones = timeZones.sort((a, b) => {
        const offsetA = moment.tz(a).utcOffset();
        const offsetB = moment.tz(b).utcOffset();
        return offsetA - offsetB;
    });

    const isValidGoogleSheetsUrl = (url) => {
        // Регулярное выражение для проверки валидности URL Google Sheets
        const googleSheetsUrlRegex = /^https:\/\/docs\.google\.com\/spreadsheets\/d\/[a-zA-Z0-9-_]+/;

        // Проверка URL с помощью регулярного выражения
        return googleSheetsUrlRegex.test(url);
    };

    const handleSpreadsheetUrlChange = (url) => {
        setSpreadsheetUrl(url);

        // Проверка валидности URL Google Sheets
        if (isValidGoogleSheetsUrl(url)) {
            // URL валиден для Google Sheets - выполнить нужные действия
            console.log('Valid Google Sheets URL');
        } else {
            // URL не является URL Google Sheets - выполнить нужные действия, например, вывод сообщения об ошибке
            console.log('Invalid Google Sheets URL');
        }
    };

    const handleCredentialFileChange = (file) => {
        setCredentialFile(file);

        // Чтение содержимого файла
        const reader = new FileReader();
        reader.onload = (event) => {
            const fileContent = event.target.result;

            // Проверка валидности содержимого файла credentials.json
            if (isValidCredentialsFile(fileContent)) {
                // Файл credentials.json валиден - выполнить нужные действия
                console.log('Valid credentials.json file');
            } else {
                // Файл credentials.json невалиден - выполнить нужные действия, например, вывод сообщения об ошибке
                console.log('Invalid credentials.json file');
            }
        };
        reader.readAsText(file);
    };

    const isValidCredentialsFile = (fileContent) => {
        // Преобразование содержимого файла в объект
        let credentials;
        try {
            credentials = JSON.parse(fileContent);
        } catch (error) {
            console.error("Error parsing credentials.json:", error);
            return false;
        }

        // Проверка наличия обязательных полей
        const requiredFields = [
            "type",
            "project_id",
            "private_key_id",
            "private_key",
            "client_email",
            "client_id",
            "auth_uri",
            "token_uri",
            "auth_provider_x509_cert_url",
            "client_x509_cert_url"
        ];

        for (const field of requiredFields) {
            if (!credentials.hasOwnProperty(field)) {
                console.error(`Missing required field in credentials.json: ${field}`);
                return false;
            }
        }

        // Проверка типа
        if (credentials.type !== "service_account") {
            console.error(`Invalid type in credentials.json: ${credentials.type}`);
            return false;
        }

        // Проверка формата приватного ключа
        const privateKeyFormatRegex = /^-----BEGIN PRIVATE KEY-----\n[\s\S]+\n-----END PRIVATE KEY-----\n$/;
        if (!privateKeyFormatRegex.test(credentials.private_key)) {
            console.error("Invalid format for private key in credentials.json");
            return false;
        }

        // Дополнительные проверки, если необходимо

        return true;
    };

