import React, { useState, useEffect } from 'react';
import { getFileType, getSourcesList, url, getFileDisplay } from './adminToServer.js';

const Test = () => {
    const [serverFiles, setServerFiles] = useState([]);
    const [sourcesList, setSourcesList] = useState([]);
    const [draggedItem, setDraggedItem] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(url + 'getAllSources');
                if (!response.ok) {
                    throw new Error('Failed to fetch sources');
                }
                const data = await response.json();
                const filesWithMetadata = data.files.map((file, index) => ({
                    id: index,
                    type: getFileType(file),
                    file
                }));
                setServerFiles(filesWithMetadata);
                console.log("All files: " + filesWithMetadata);
            } catch (error) {
                console.error('Error fetching sources:', error);
            }
        };

        fetchData();
    }, []);

    useEffect(() => {
        const fetchSourcesList = async () => {
            try {
                const response = await getSourcesList('links.txt');
                const sources = response.sourcesList;
                setSourcesList(sources);
            } catch (error) {
                console.error('Error fetching sources list:', error);
            }
        };

        fetchSourcesList();
    }, []);

    const handleMoveToServer = (item) => {
        setServerFiles([...serverFiles, item]);
        setSourcesList(sourcesList.filter(source => source.id !== item.id));
    };

    const handleMoveToSources = (item) => {
        setSourcesList([...sourcesList, item]);
        setServerFiles(serverFiles.filter(file => file.id !== item.id));
    };

    const handleDragStart = (event, item) => {
        setDraggedItem(item);
    };

    const handleDragOver = (event) => {
        event.preventDefault();
    };

    const handleDrop = (event, target) => {
        event.preventDefault();
        if (draggedItem) {
            if (target === 'serverFiles') {
                handleMoveToServer(draggedItem);
            } else if (target === 'sourcesList') {
                handleMoveToSources(draggedItem);
            }
            setDraggedItem(null);
        }
    };

    const handleDropInsideTable = (event) => {
        event.preventDefault();
        if (draggedItem) {
            const fromIndex = draggedItem.id;
            let toIndex = Number(event.target.dataset.id);
            if (isNaN(toIndex)) {
                toIndex = Number(event.target.parentElement.dataset.id);
            }

            const updatedList = [...serverFiles];
            const item = updatedList.splice(fromIndex, 1)[0];
            updatedList.splice(toIndex, 0, item);
            setServerFiles(updatedList);
            setDraggedItem(null);
        }
    };


    return (
        <div>
            <h2>Server Files</h2>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Type</th>
                        <th>File</th>
                    </tr>
                </thead>
                <tbody onDragOver={(event) => handleDragOver(event)} onDrop={(event) => handleDrop(event, 'serverFiles')}>
                    {serverFiles.map(item => (
                        <tr key={item.id} draggable onDragStart={(event) => handleDragStart(event, item)} onDrop={(event) => handleDropInsideTable(event, 'serverFiles')}>
                            <td>{item.id}</td>
                            <td>{item.type}</td>
                            <td>{getFileDisplay(item)}</td>
                            <td>
                                <button onClick={() => handleMoveToSources(item)}>Move to Sources</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Sources List</h2>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Type</th>
                        <th>File</th>
                    </tr>
                </thead>
                <tbody onDragOver={(event) => handleDragOver(event)} onDrop={(event) => handleDrop(event, 'sourcesList')}>
                    {sourcesList.map(item => (
                        <tr key={item.id} draggable onDragStart={(event) => handleDragStart(event, item)}>
                            <td>{item.id}</td>
                            <td>{item.type}</td>
                            <td>{getFileDisplay(item)}</td>
                            <td>
                                <button onClick={() => handleMoveToServer(item)}>Move to Server</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Test;
